<script>
  document.addEventListener('DOMContentLoaded', function () {
    const screen1 = document.getElementById('screen-1');
    const screen2 = document.getElementById('screen-2');
    const unwrapBtn = document.getElementById('unwrapBtn');
    const backToScreen1 = document.getElementById('backToScreen1');

    const claimBtn = document.getElementById('claimBtn');
    const claimForm = document.getElementById('claimForm');
    const backToMenu = document.getElementById('backToMenu');
    const menuButtons = document.getElementById('menuButtons');

    function show(el) { if (el) el.hidden = false; }
    function hide(el) { if (el) el.hidden = true; }

    // Check if returning from "How to Join" page - show menu
    const urlParams = new URLSearchParams(window.location.search);
    if (urlParams.get('menu') === '1') {
      hide(screen1);
      show(screen2);
      // Clean up URL
      window.history.replaceState({}, document.title, window.location.pathname);
    }

    // Check if returning from verify page - show form with pre-filled data
    if (urlParams.get('edit') === '1') {
      hide(screen1);
      show(screen2);
      show(claimForm);
      hide(menuButtons);
      hide(backToScreen1);

      // Pre-fill form fields
      const firstName = urlParams.get('first_name');
      const lastName = urlParams.get('last_name');
      const phone = urlParams.get('phone');

      if (firstName) document.getElementById('first_name').value = decodeURIComponent(firstName);
      if (lastName) document.getElementById('last_name').value = decodeURIComponent(lastName);
      if (phone) document.getElementById('mobile_number').value = decodeURIComponent(phone);

      // Check terms checkbox
      document.getElementById('accept_terms').checked = true;

      // Clean up URL
      window.history.replaceState({}, document.title, window.location.pathname);
    }

    if (unwrapBtn) {
      unwrapBtn.addEventListener('click', function (e) {
        e.preventDefault();
        hide(screen1);
        show(screen2);
      });
    }

    if (backToScreen1) {
      backToScreen1.addEventListener('click', function (e) {
        e.preventDefault();
        hide(claimForm);
        show(menuButtons);
        show(backToScreen1);
        show(screen1);
        hide(screen2);
      });
    }

    if (claimBtn) {
      claimBtn.addEventListener('click', function (e) {
        e.preventDefault();

        // Check if QR code is already scanned
        @if($qrCode->is_scanned)
          // Redirect to already-scanned page
          window.location.href = '{{ route("qr.already-scanned", ["code" => $qrCode->code]) }}';
          return;
        @endif

        show(claimForm);
        hide(menuButtons);
        hide(backToScreen1);
      });
    }

    if (backToMenu) {
      backToMenu.addEventListener('click', function (e) {
        e.preventDefault();
        hide(claimForm);
        show(menuButtons);
        show(backToScreen1);
      });
    }

    // Hide preloader when page is loaded
    window.addEventListener('load', function() {
      const preloader = document.getElementById('preloader');
      if (preloader) {
        preloader.classList.add('hidden');
        setTimeout(function() {
          preloader.style.display = 'none';
        }, 500);
      }
    });
  });
</script>


